INSERT INTO public.stories (slug, title, image, "dataAiHint", category, excerpt, author, "authorAvatar", date, "clientImage", location, project, size, quote, content, gallery) VALUES
('modern-apartment-makeover', 'Modern Apartment Makeover in Koregaon Park', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-1-main.jpg', 'modern apartment', 'Apartment', 'See how we transformed a standard 2BHK apartment into a modern, functional, and stylish living space for the Sharma family.', 'Priya Sharma', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/team-2.jpg', '2023-11-15', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-1-client.jpg', 'Koregaon Park, Pune', 'Full Home Interior', '2 BHK', 'Our home feels brand new! Shriram Interio''s team was exceptional.', '<p>The Sharma family approached us with a common challenge: their 2BHK apartment felt cramped and outdated. They wanted a home that was modern, spacious, and reflected their vibrant personality. Our design team, led by Priya Sharma, took on the challenge with enthusiasm.</p><p>We started by creating an open-plan living area, removing a non-structural wall between the kitchen and living room. This simple change instantly made the space feel larger and more inviting. We chose a neutral color palette with pops of bold colors to add character without overwhelming the space.</p><p>The kitchen was completely redesigned with sleek, handle-less cabinets, quartz countertops, and smart storage solutions. In the master bedroom, we designed a custom wardrobe with a built-in study nook, maximizing functionality in a compact area. The result is a home that is not only beautiful but also perfectly tailored to the Sharma family''s lifestyle.</p>', '[{"src": "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-1-gallery-1.jpg", "alt": "Living Room Before", "dataAiHint": "living room"}, {"src": "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-1-gallery-2.jpg", "alt": "Living Room After", "dataAiHint": "modern living room"}, {"src": "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-1-gallery-3.jpg", "alt": "Kitchen After", "dataAiHint": "modern kitchen"}]'),
('luxurious-villa-design', 'Luxurious Villa Design in Hinjewadi', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-2-main.jpg', 'luxury villa', 'Villa', 'A breathtaking transformation of a 4BHK villa into a luxurious haven of comfort and style.', 'Rajesh Kumar', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/team-3.jpg', '2023-09-22', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-2-client.jpg', 'Hinjewadi, Pune', 'Full Home Interior', '4 BHK Villa', 'Every detail is perfect. It''s like living in a five-star resort.', 'The Patel family wanted their new villa to be a statement of luxury and elegance. Our team, managed by Rajesh Kumar, crafted a design that blended classic elements with contemporary style. We used a rich color palette, premium materials like marble and walnut wood, and designer lighting to create a sophisticated ambiance. The project included a home theatre, a custom bar, and a beautifully landscaped terrace garden, making it a true masterpiece of design and craftsmanship.', '[{"src": "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-2-gallery-1.jpg", "alt": "Grand Living Area", "dataAiHint": "luxury living room"}, {"src": "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-2-gallery-2.jpg", "alt": "Master Bedroom", "dataAiHint": "luxury bedroom"}, {"src": "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/story-2-gallery-3.jpg", "alt": "Home Theatre", "dataAiHint": "home theater"}]');

