INSERT INTO public.products (name, slug, short_description, long_description, main_image, features, gallery, amazon_link) VALUES
('Modular Kitchen', 'modular-kitchen', 'High-quality, stylish, and functional modular kitchens.', 'Our modular kitchens are designed for modern living. With a focus on functionality and aesthetics, we use high-quality materials and smart storage solutions to create a kitchen that is both beautiful and easy to use. Choose from a variety of finishes and layouts to suit your space and style.', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/product-kitchen.jpg', '["Durable Plywood Construction", "Soft-Close Hinges & Drawers", "Scratch-Resistant Finishes", "Customizable Layouts", "Smart Storage Solutions", "5-Year Warranty"]', '["https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/kitchen-gallery-1.jpg", "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/kitchen-gallery-2.jpg"]', 'https://www.amazon.in/'),
('Custom Wardrobe', 'custom-wardrobe', 'Bespoke wardrobes designed to maximize storage and style.', 'Our custom wardrobes are tailored to your specific needs and space. Whether you need a walk-in closet or a compact wardrobe, we design solutions that maximize storage and complement your bedroom''s decor. Choose from a range of materials, finishes, and accessories to create your perfect wardrobe.', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/product-wardrobe.jpg', '["Personalized Design", "Maximized Storage Space", "Variety of Finishes", "High-Quality Hardware", "Integrated Lighting Options", "Durable Materials"]', '["https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/wardrobe-gallery-1.jpg", "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/wardrobe-gallery-2.jpg"]', 'https://www.amazon.in/'),
('Living Room Design', 'living-room-design', 'Create an inviting and stylish living space for your family.', 'The living room is the heart of your home. Our designers work with you to create a space that is comfortable, functional, and reflects your personal style. From furniture selection to lighting and decor, we take care of every detail to create a living room you''ll love.', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/product-living.jpg', '["Custom Furniture Design", "Space Planning & Layout", "Lighting Design", "Color Palette Selection", "Flooring & Rugs", "Wall Treatments & Decor"]', '["https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/living-gallery-1.jpg", "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/living-gallery-2.jpg"]', NULL),
('Bedroom Interior', 'bedroom-interior', 'Design a peaceful and personal sanctuary for rest and relaxation.', 'Your bedroom should be a sanctuary. We design bedroom interiors that are not only beautiful but also promote rest and relaxation. From custom beds and wardrobes to lighting and decor, we create a personalized space that is your perfect retreat.', 'https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/product-bedroom.jpg', '["Custom Bed & Headboard", "Wardrobe & Storage Design", "Soothing Color Schemes", "Ambient & Task Lighting", "Cozy Textiles & Furnishings", "Personalized Decor"]', '["https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/bedroom-gallery-1.jpg", "https://gzlakbpbhhxxpzbbifus.supabase.co/storage/v1/object/public/public/bedroom-gallery-2.jpg"]', NULL);

