
-- Create the 'pages' table
CREATE TABLE public.pages (
    id bigint NOT NULL,
    slug text NOT NULL,
    title text,
    meta_title text,
    meta_description text
);
ALTER TABLE public.pages ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY (
    SEQUENCE NAME public.pages_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);
ALTER TABLE ONLY public.pages ADD CONSTRAINT pages_pkey PRIMARY KEY (id);
ALTER TABLE ONLY public.pages ADD CONSTRAINT pages_slug_key UNIQUE (slug);

-- Create the 'sections' table
CREATE TABLE public.sections (
    id bigint NOT NULL,
    page_id bigint,
    type text NOT NULL,
    title text,
    content jsonb,
    "order" integer,
    visible boolean DEFAULT true,
    content_structure jsonb
);
ALTER TABLE public.sections ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY (
    SEQUENCE NAME public.sections_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);
ALTER TABLE ONLY public.sections ADD CONSTRAINT sections_pkey PRIMARY KEY (id);
ALTER TABLE ONLY public.sections ADD CONSTRAINT sections_page_id_fkey FOREIGN KEY (page_id) REFERENCES public.pages(id);

-- Create the 'stories' table
CREATE TABLE public.stories (
    id bigint NOT NULL,
    slug text NOT NULL,
    title text NOT NULL,
    category text,
    image text,
    "dataAiHint" text,
    author text,
    "authorAvatar" text,
    date text,
    excerpt text,
    "clientImage" text,
    location text,
    project text,
    size text,
    quote text,
    content text,
    gallery jsonb
);
ALTER TABLE public.stories ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY (
    SEQUENCE NAME public.stories_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);
ALTER TABLE ONLY public.stories ADD CONSTRAINT stories_pkey PRIMARY KEY (id);
ALTER TABLE ONLY public.stories ADD CONSTRAINT stories_slug_key UNIQUE (slug);


