-- Enable Row Level Security
alter table pages enable row level security;
alter table sections enable row level security;
alter table stories enable row level security;
alter table products enable row level security;

-- Create policies for pages
drop policy if exists "Allow public read access to pages" on pages;
create policy "Allow public read access to pages" on pages for select using (true);

-- Create policies for sections
drop policy if exists "Allow public read access to sections" on sections;
create policy "Allow public read access to sections" on sections for select using (true);

-- Create policies for stories
drop policy if exists "Allow public read access to stories" on stories;
create policy "Allow public read access to stories" on stories for select using (true);

-- Create policies for products
drop policy if exists "Allow public read access to products" on products;
create policy "Allow public read access to products" on products for select using (true);


-- Create storage bucket for public images
insert into storage.buckets (id, name, public)
values ('public', 'public', true)
on conflict (id) do nothing;

-- Create storage bucket policies
drop policy if exists "Allow public read access to public bucket" on storage.objects;
create policy "Allow public read access to public bucket" on storage.objects for select to public using (bucket_id = 'public');

drop policy if exists "Allow authenticated uploads to public bucket" on storage.objects;
create policy "Allow authenticated uploads to public bucket" on storage.objects for insert to authenticated with check (bucket_id = 'public');


-- Create tables
drop table if exists stories cascade;
create table if not exists stories (
  id serial primary key,
  slug text unique not null,
  title text not null,
  image text not null,
  "dataAiHint" text,
  category text not null,
  excerpt text not null,
  author text not null,
  "authorAvatar" text,
  date text not null,
  "clientImage" text,
  location text,
  project text,
  size text,
  quote text,
  content text,
  gallery jsonb
);

drop table if exists products cascade;
create table if not exists products (
    id serial primary key,
    name text not null,
    slug text unique not null,
    short_description text,
    long_description text,
    main_image text,
    features text[],
    gallery text[],
    amazon_link text
);

